package 환자관리;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.border.EtchedBorder;//환자 정보 조회를 위한 UI와 검색처리public class FindPane extends JPanel implements ActionListener { private JPanel[] jp = new JPanel[7]; private JLabel[] jl = new JLabel[6]; private JTextField[] tf = new JTextField[6]; private JButton okb; private JButton rsb;  String[] caption = {"사 번 :","이 름 :","생 일 :","메 일 :","번 호 :","성 별 :"};  public FindPane() {  this.setLayout(new GridLayout(8,1));  EtchedBorder eb = new EtchedBorder();  this.setBorder(eb);    int size = caption.length;    for(int i=0;i<size;i++) {   jl[i] = new JLabel(caption[i]);   tf[i] = new JTextField(15);   jp[i] = new JPanel();   jp[i].add(jl[i]);   jp[i].add(tf[i]);   this.add(jp[i]);      tf[i].setEditable(false);   if(i==0 || i==1) {    tf[i].setEditable(true);   }  }  jp[size] = new JPanel();  okb = new JButton("환자조회");  okb.addActionListener(this);  rsb = new JButton("다시쓰기");  rsb.addActionListener(this);  jp[size].add(okb);  jp[size].add(rsb);  this.add(jp[size]); }
